import { useState, useEffect } from "react";
import { useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Task, InsertTask } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface EditTaskModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  task: Task | null;
}

export function EditTaskModal({ open, onOpenChange, task }: EditTaskModalProps) {
  const [formData, setFormData] = useState<{
    title: string;
    description: string;
    priority: "low" | "medium" | "high";
    category: "genel" | "turkce" | "sosyal" | "matematik" | "fizik" | "kimya" | "biyoloji" | "tyt-geometri" | "ayt-geometri" | "ayt-matematik" | "ayt-fizik" | "ayt-kimya" | "ayt-biyoloji";
    color: string;
    dueDate: string;
  }>({
    title: "",
    description: "",
    priority: "medium",
    category: "genel",
    color: "#8B5CF6",
    dueDate: new Date().toISOString().split('T')[0],
  });

  const { toast } = useToast();

  // GÃ¶rev deÄŸiÅŸtiÄŸinde form verilerini gÃ¼ncelle
  useEffect(() => {
    if (task) {
      setFormData({
        title: task.title || "",
        description: task.description || "",
        priority: task.priority || "medium",
        category: task.category || "genel",
        color: task.color || "#8B5CF6",
        dueDate: task.dueDate ? task.dueDate.split('T')[0] : new Date().toISOString().split('T')[0],
      });
    }
  }, [task]);

  const updateTaskMutation = useMutation({
    mutationFn: (data: { id: string; updates: Partial<InsertTask> }) => 
      apiRequest("PUT", `/api/tasks/${data.id}`, data.updates),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tasks"] });
      queryClient.invalidateQueries({ queryKey: ["/api/calendar"] });
      toast({
        title: "GÃ¶rev gÃ¼ncellendi",
        description: "GÃ¶rev baÅŸarÄ±yla gÃ¼ncellendi.",
      });
      onOpenChange(false);
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "GÃ¶rev gÃ¼ncellenemedi.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!task) return;

    if (!formData.title.trim()) {
      toast({
        title: "UyarÄ±",
        description: "GÃ¶rev baÅŸlÄ±ÄŸÄ± gereklidir.",
        variant: "destructive",
      });
      return;
    }

    updateTaskMutation.mutate({
      id: task.id,
      updates: {
        title: formData.title.trim(),
        description: formData.description.trim() || undefined,
        priority: formData.priority,
        category: formData.category,
        color: formData.color,
        dueDate: formData.dueDate,
      }
    });
  };

  const handleCancel = () => {
    onOpenChange(false);
  };

  if (!task) return null;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-lg">
        <DialogHeader>
          <DialogTitle>GÃ¶revi DÃ¼zenle</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* GÃ¶rev BaÅŸlÄ±ÄŸÄ± */}
          <div>
            <Label htmlFor="edit-task-title">GÃ¶rev BaÅŸlÄ±ÄŸÄ±</Label>
            <Input
              id="edit-task-title"
              placeholder="GÃ¶rev baÅŸlÄ±ÄŸÄ±nÄ± girin..."
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              data-testid="input-edit-task-title"
            />
          </div>

          {/* GÃ¶rev AÃ§Ä±klamasÄ± */}
          <div>
            <Label htmlFor="edit-task-description">AÃ§Ä±klama</Label>
            <Textarea
              id="edit-task-description"
              placeholder="GÃ¶rev detaylarÄ±nÄ± aÃ§Ä±klayÄ±n..."
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="h-20 resize-none"
              data-testid="input-edit-task-description"
            />
          </div>

          {/* Ã–ncelik & Kategori */}
          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="edit-task-priority">Ã–ncelik</Label>
              <Select
                value={formData.priority}
                onValueChange={(value: "low" | "medium" | "high") => 
                  setFormData(prev => ({ ...prev, priority: value }))
                }
              >
                <SelectTrigger data-testid="select-edit-task-priority">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">DÃ¼ÅŸÃ¼k</SelectItem>
                  <SelectItem value="medium">Orta</SelectItem>
                  <SelectItem value="high">YÃ¼ksek</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="edit-task-category">Ders Kategorisi</Label>
              <Select
                value={formData.category}
                onValueChange={(value: "genel" | "turkce" | "sosyal" | "matematik" | "fizik" | "kimya" | "biyoloji" | "tyt-geometri" | "ayt-geometri" | "ayt-matematik" | "ayt-fizik" | "ayt-kimya" | "ayt-biyoloji") => 
                  setFormData(prev => ({ ...prev, category: value }))
                }
              >
                <SelectTrigger data-testid="select-edit-task-category">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="genel">Genel</SelectItem>
                  <div className="px-2 py-1 text-xs font-semibold text-muted-foreground border-b">TYT Dersleri</div>
                  <SelectItem value="turkce">TÃ¼rkÃ§e</SelectItem>
                  <SelectItem value="sosyal">Sosyal Bilimler</SelectItem>
                  <SelectItem value="matematik">Matematik</SelectItem>
                  <SelectItem value="fizik">Fizik</SelectItem>
                  <SelectItem value="kimya">Kimya</SelectItem>
                  <SelectItem value="biyoloji">Biyoloji</SelectItem>
                  <SelectItem value="tyt-geometri">TYT Geometri</SelectItem>
                  <div className="px-2 py-1 text-xs font-semibold text-muted-foreground border-b border-t">AYT Dersleri</div>
                  <SelectItem value="ayt-matematik">AYT Matematik</SelectItem>
                  <SelectItem value="ayt-fizik">AYT Fizik</SelectItem>
                  <SelectItem value="ayt-kimya">AYT Kimya</SelectItem>
                  <SelectItem value="ayt-biyoloji">AYT Biyoloji</SelectItem>
                  <SelectItem value="ayt-geometri">AYT Geometri</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Son Tarih */}
          <div>
            <Label htmlFor="edit-task-due-date">Son Tarih</Label>
            <Input
              id="edit-task-due-date"
              type="date"
              value={formData.dueDate}
              onChange={(e) => setFormData(prev => ({ ...prev, dueDate: e.target.value }))}
              className="w-full"
              data-testid="input-edit-task-due-date"
            />
          </div>

          {/* Renk SeÃ§ici */}
          <div>
            <Label htmlFor="edit-task-color">GÃ¶rev Rengi</Label>
            <div className="flex items-center space-x-3">
              <Input
                id="edit-task-color"
                type="color"
                value={formData.color}
                onChange={(e) => setFormData(prev => ({ ...prev, color: e.target.value }))}
                className="w-16 h-10 rounded cursor-pointer"
                data-testid="input-edit-task-color"
              />
              <div className="flex space-x-2">
                {["#8B5CF6", "#EC4899", "#10B981", "#F59E0B", "#EF4444", "#3B82F6"].map((color) => (
                  <button
                    key={color}
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, color }))}
                    className={`w-6 h-6 rounded-full border-2 ${
                      formData.color === color ? "border-gray-400" : "border-transparent"
                    }`}
                    style={{ backgroundColor: color }}
                    title={color}
                  />
                ))}
              </div>
            </div>
          </div>

          {/* Eylem DÃ¼ÄŸmeleri */}
          <div className="flex space-x-3 pt-4">
            <Button 
              type="submit"
              disabled={updateTaskMutation.isPending}
              className="flex-1"
              data-testid="button-update-task"
            >
              {updateTaskMutation.isPending ? "GÃ¼ncelleniyor..." : "GÃ¼ncelle"}
            </Button>
            <Button 
              type="button"
              variant="secondary"
              onClick={handleCancel}
              className="flex-1"
              data-testid="button-cancel-edit-task"
            >
              Ä°ptal
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}
