interface AYTData {
  matematik: { dogru: number; yanlis: number; };
  fizik: { dogru: number; yanlis: number; };
  kimya: { dogru: number; yanlis: number; };
  biyoloji: { dogru: number; yanlis: number; };
}

interface OBPData {
