      // E-postayƒ± g√∂nder
      if (process.env.EMAIL_USER && process.env.EMAIL_PASS) {
        try {
          const info = await transporter.sendMail(mailOptions);
          console.log("Email sent successfully:", info.messageId);
          res.json({
            message: "Rapor ba≈üarƒ±yla e-posta adreslerine g√∂nderildi!",
          });
        } catch (emailError) {
          console.error("Email sending failed:", emailError);

          // Email hatasƒ±nƒ±n t√ºr√ºne g√∂re farklƒ± mesajlar
          if (
            emailError.code === "ENOTFOUND" ||
            emailError.code === "ECONNECTION"
          ) {
            res.status(500).json({
              message:
                "Email servisi ile baƒülantƒ± kurulamadƒ±. L√ºtfen internet baƒülantƒ±nƒ±zƒ± kontrol edin.",
            });
          } else if (emailError.responseCode === 550) {
