            wrongAnswers: 3,
            totalExams: 1,
            totalTasks: 12,
            totalActivities: 20,
          },
        },
        gorev_tamamlandi: {
          subject: "üéâ G√∂revler Tamamlandƒ± - Test",
          reportData: {
            totalQuestions: 40,
            correctAnswers: 35,
            wrongAnswers: 5,
            totalExams: 3,
            totalTasks: 15,
            totalActivities: 25,
          },
        },
        aylik_ozet: {
          subject: "üìä Aylƒ±k √ñzet Raporu - Test",
          reportData: {
            totalQuestions: 200,
            correctAnswers: 160,
            wrongAnswers: 40,
            totalExams: 8,
            totalTasks: 25,
            totalActivities: 50,
          },
        },
      };

      const scenario = testScenarios[testType] || testScenarios.aylik_ozet;

      // PDF olu≈ütur
      const doc = new PDFKit({
        size: "A4",
        margin: 40,
        bufferPages: true,
      });

      const buffers = [];
      doc.on("data", buffers.push.bind(buffers));
      doc.on("end", () => {
        const pdfBuffer = Buffer.concat(buffers);

        // Gmail SMTP konfig√ºrasyonu
        const transporter = nodemailer.createTransport({
          service: "gmail",
