        // E-postayƒ± g√∂nder
        if (process.env.EMAIL_USER && process.env.EMAIL_PASS) {
          transporter
            .sendMail(mailOptions)
            .then(() => {
              res.json({
                message: `Test e-postasƒ± ba≈üarƒ±yla g√∂nderildi! Test t√ºr√º: ${testType || "aylik_ozet"}`,
                testType: testType || "aylik_ozet",
                emailSent: true,
              });
            })
            .catch((emailError) => {
              console.error("Test email g√∂nderim hatasƒ±:", emailError);
              res.status(500).json({
