                testType: testType || "aylik_ozet",
                emailSent: false,
              });
            });
        } else {
          res.json({
            message:
              "E-posta kimlik bilgileri yapƒ±landƒ±rƒ±lmamƒ±≈ü - Test PDF olu≈üturuldu",
            testType: testType || "aylik_ozet",
            emailSent: false,
          });
        }
      });

      // PDF i√ßeriƒüi olu≈ütur
      generatePDFContent(doc, scenario.reportData);
      doc.end();
    } catch (error) {
      console.error("Test email error:", error);
      res
        .status(500)
        .json({ message: "Test e-posta g√∂nderiminde hata olu≈ütu" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

