  .rain-animation {
    background: linear-gradient(transparent 0%, rgba(59, 130, 246, 0.1) 100%);
    position: relative;
  }

  .rain-animation::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
      linear-gradient(90deg, rgba(59, 130, 246, 0.3) 1px, transparent 1px),
      linear-gradient(180deg, rgba(59, 130, 246, 0.3) 1px, transparent 1px);
    background-size: 20px 20px;
    animation: rain-fall 2s linear infinite;
    opacity: 0.3;
  }

  .sun-rays-animation {
    background: radial-gradient(circle, rgba(251, 191, 36, 0.1) 0%, transparent 70%);
    position: relative;
    overflow: hidden;
  }

  .sun-rays-animation::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: conic-gradient(from 0deg, transparent, rgba(251, 191, 36, 0.1), transparent, rgba(251, 191, 36, 0.05), transparent);
    animation: sun-rotation 20s linear infinite;
    pointer-events: none;
  }

  @keyframes rain-fall {
    0% {
      background-position: 0 -20px;
    }
    100% {
      background-position: 0 20px;
    }
  }

  @keyframes sun-rotation {
    0% {
      transform: rotate(0deg);
    }
    100% {
      transform: rotate(360deg);
    }
  }

  /* Kƒ±≈üƒ±n kar yaƒüarsa hava durumu i√ßin kar animasyonu */
  .snow-animation {
    background: linear-gradient(transparent 0%, rgba(219, 234, 254, 0.1) 100%);
