            <div className="flex items-center gap-3">
              <Badge variant="secondary" className="bg-gradient-to-r from-muted/80 to-muted/60 text-foreground font-medium px-3 py-1 shadow-sm">
                <Sparkles className="h-3 w-3 mr-1" />
