              <Input
                value={userAnswer}
                onChange={(e) => setUserAnswer(e.target.value)}
                placeholder="Cevabƒ± buraya yazƒ±n..."
                onKeyPress={(e) => e.key === 'Enter' && handleAnswerSubmit()}
