  const generateYearlyHeatmapData = () => {
    const data = [];
    const today = new Date();
    const currentYear = today.getFullYear();

    // Bu yƒ±lƒ±n 1 Ocak tarihinden itibaren ba≈ülayƒ±n.
    const startDate = new Date(currentYear, 0, 1); // ocak ayƒ± 0 indekstir.

    // 1 Ocak'tan bug√ºne kadar olan verileri olu≈ütur (bug√ºn dahil)
    const currentDate = new Date(startDate);
    const todayDateStr = today.getFullYear() + '-' + 
      String(today.getMonth() + 1).padStart(2, '0') + '-' + 
      String(today.getDate()).padStart(2, '0');

    // Bug√ºn√º de dahil edene kadar devam edin
    while (currentDate.toISOString().split('T')[0] <= todayDateStr) {
      const dateStr = currentDate.toISOString().split('T')[0];

      // Bu g√ºn i√ßin aktivite yoƒüunluƒüunu hesaplayƒ±n
      const dayQuestions = questionLogs.filter(log => log.study_date === dateStr);
