        const completedDate = new Date(task.completedAt).toISOString().split('T')[0];
        return completedDate === dateStr;
      });

      const studyIntensity = Math.min((dayQuestions.length * 2 + dayTasks.length) / 10, 1);

      // Bug√ºn olup olmadƒ±ƒüƒ±nƒ± kontrol et - Sabit kar≈üƒ±la≈ütƒ±rma
      const isToday = dateStr === todayDateStr;

      data.push({
        date: dateStr,
        day: currentDate.getDate(),
        month: currentDate.getMonth(),
        dayOfWeek: currentDate.getDay(), // 0 = Pazar, 1 = Pazartesi, vsvs.
        dayOfWeekISO: currentDate.getDay() === 0 ? 7 : currentDate.getDay(), // 1 = Pazartesi, 7 = Pazar
        intensity: studyIntensity,
        count: dayQuestions.length + dayTasks.length,
        questionCount: dayQuestions.length,
        taskCount: dayTasks.length,
        isToday: isToday
      });

      // Bir sonraki g√ºne ge√ß
      currentDate.setDate(currentDate.getDate() + 1);
    }

    return data;
  };

  // Isƒ± haritasƒ± verilerini haftalara organize et (Pazartesi'den Pazar'a)
  const organizeHeatmapIntoWeeks = (data: any[]) => {
    const weeks = [];

    if (data.length === 0) return weeks;

    // Tarihe g√∂re hƒ±zlƒ± arama i√ßin bir harita olu≈üturun
    const dateMap = new Map();
    data.forEach(day => {
      dateMap.set(day.date, day);
    });

    // ƒ∞lk tarihi bulun ve o haftanƒ±n Pazartesi g√ºn√ºn√º hesaplayƒ±n.
    const firstDate = new Date(data[0].date);
    const firstDayOfWeek = firstDate.getDay(); // 0 = Pazar, 1 = Pazartesi, ...
    const daysToMonday = firstDayOfWeek === 0 ? 6 : firstDayOfWeek - 1; // Pazartesi'ye geri gitmek i√ßin g√ºnleri hesaplayƒ±n

    // ƒ∞lk haftanƒ±n Pazartesi g√ºn√ºnden ba≈ülayƒ±n
    const startDate = new Date(firstDate);
    startDate.setDate(firstDate.getDate() - daysToMonday);

    // Son tarihi bulun ve o haftanƒ±n Pazar g√ºn√ºn√º hesaplayƒ±n.
    const lastDate = new Date(data[data.length - 1].date);
    const lastDayOfWeek = lastDate.getDay();
    const daysToSunday = lastDayOfWeek === 0 ? 0 : 7 - lastDayOfWeek; // Pazar'a gitmek i√ßin g√ºnleri hesaplayƒ±n

    const endDate = new Date(lastDate);
    endDate.setDate(lastDate.getDate() + daysToSunday);

    // Pazartesi ba≈ülangƒ±√ßlƒ± ve Pazar biti≈üli haftalar olu≈üturun
    const currentDate = new Date(startDate);

    while (currentDate <= endDate) {
      const week = [];

      // Bu hafta i√ßin 7 g√ºn olu≈üturun (Pazartesi'den Pazar'a)
      for (let i = 0; i < 7; i++) {
        const dateStr = currentDate.toISOString().split('T')[0];
        const dayData = dateMap.get(dateStr);

        week.push(dayData || null);
        currentDate.setDate(currentDate.getDate() + 1);
      }

      weeks.push(week);
    }

    return weeks;
  };

