            </CardHeader>
            <CardContent className="p-6">
              {/* Ta≈ümayƒ± √∂nlemek i√ßin optimize edilmi≈ü konteyner */}
              <div className="w-full overflow-hidden">
                <div className="flex flex-col items-center max-w-full">
                  {/* Konteyner geni≈üliƒüine g√∂re optimize edilmi≈ü ay etiketleri */}
                  <div className="w-full mb-4">
                    <div className="flex justify-start">
                      <div className="w-10"></div> {/* G√ºn etiketleri i√ßin alan */}
                      <div className="flex-1 relative h-8">
                        {(() => {
                          const monthNames = [
                            'Ocak', '≈ûubat', 'Mart', 'Nisan', 'Mayƒ±s', 'Haziran',
                            'Temmuz', 'Aƒüustos', 'Eyl√ºl', 'Ekim', 'Kasƒ±m', 'Aralƒ±k'
                          ];
                          const currentMonth = new Date().getMonth();
                          const monthLabels = [];

                          // Her ayƒ±n hangi haftalarƒ± kapsadƒ±ƒüƒ±nƒ± hesaplayƒ±n
                          const monthWeekMap = new Map();

                          heatmapWeeks.forEach((week, weekIndex) => {
